            form:{
                cityMoney:'7086',
                beginTime:'',
                endTime:'',
                salary:null
            },
            citys,
            resultObj: {
                money: '',
                month: ''
            },
            rules: {
                beginTime:[
                    {type: 'string', required: true, message: '请选择开始时间'}
                ],
                endTime:[
                    {type: 'string', required: true, message: '请选择结束时间'}
                ],
                salary:[
                    {required: true, message: '请输入平均工资'},
                    {type: 'number', required: true, message: '工资只能输入数值'}
                ]
            }
        }
    },
    methods: {

        countHandler(){
            this.$refs['countForm'].validate(valid => {
                if (valid){
                    var diffDay = this.dateDiff(this.form.beginTime,this.form.endTime);
                    var wages = parseFloat(this.form.salary)
                    if(diffDay <= 0){
                        this.$Message.error('结束时间不能小于开始时间')
                        return;
                    }
                    
                    var month = Math.floor(diffDay / 360);
                    var surplusDayNum = diffDay % 360;

                    if(surplusDayNum >= 180) {
                        month++;
                    }
                    if(surplusDayNum > 30 && surplusDayNum < 180) {
                        month += 0.5;
                    }
                    // if(wages < city) wages = city;
                    if(wages > parseFloat(this.form.city_money)*3){
                        wages = parseFloat(this.form.city_money)*3;
                    } 

                    var money = wages * month;
                    this.resultObj = {
                        money: parseFloat(money).toFixed(2),
                        month
                    }
                }
            })
        },
        resetHandler(){

        }
    }
}
